mapper.select:
SELECT

FROM ts_inventory a,
     ts_inventory_item b,
     ts_supplier s,
     sys_office o  

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND b.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = 	b.inventory_id	
	AND a.supplier_id = s.id
	AND a.company_id = o.id

		AND a.company_id = #{companyId}


		AND a.inventory_no LIKE 
			'%'||#{inventoryNo}||'%'
			'%'+#{inventoryNo}+'%'
			concat('%',#{inventoryNo},'%')


		AND a.supplier_id = #{supplierId}


		AND a.type = #{type}


		AND b.goods_type = #{goodsType}


		 AND DATE_FORMAT(a.inventory_date,'%Y-%m-%d') >= DATE_FORMAT(#{inventoryDateBegin},'%Y-%m-%d')    				  


		 AND DATE_FORMAT(a.inventory_date,'%Y-%m-%d') <= DATE_FORMAT(#{inventoryDateEnd},'%Y-%m-%d')    				  			




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
