mapper.select:
SELECT 

FROM ts_inventory a

WHERE a.id = #{id}

SELECT 

FROM ts_inventory a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.inventory_no = #{inventoryNo}


		AND a.supplier_id = #{supplierId}


		AND a.inventory_date = #{inventoryDate}


		AND a.total_price = #{totalPrice}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ts_inventory a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ts_inventory SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
