mapper.select:
SELECT 

FROM ts_supplier a

WHERE a.id = #{id}

SELECT 

FROM ts_supplier a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.supplier_code = #{supplierCode}


		AND a.supplier_name = #{supplierName}


		AND a.type = #{typeString}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ts_supplier a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ts_supplier SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
